import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import styled from "styled-components";
import loader from "../assets/loader.gif";
import { ToastContainer, toast } from "react-toastify";
import axios from "axios";
import { Buffer } from "buffer";

import "react-toastify/dist/ReactToastify.css";
import { setAvatarRoute } from "../utils/APIRoutes";

// Set Avatar
const SetAvatar = () => {
  const API_URI = "https://api.multiavatar.com"; // Api url
  const navigate = useNavigate();
  const [avatars, setAvatars] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const [selectedAvatar, setSelectedAvatar] = useState(undefined);

  // Show toast error message
  const showToast = (msg) => {
    const toastOptions = {
      position: "bottom-right",
      autoClose: 8000,
      pauseOnHover: true,
      draggable: true,
      theme: "light",
    };

    return toast.error(msg, toastOptions);
  };

  // Check user login
  useEffect(() => {
    if (!localStorage.getItem(process.env.USER))
      return navigate("/login");
    const user = JSON.parse(
      localStorage.getItem(process.env.USER)
    );
    if (user.isAvatarImageSet) return navigate("/");
  }, []);  

  // set profile picture
  const setProfilePicture = async () => {
    if (selectedAvatar === undefined) {
      showToast("Please select an avatar");
    } else {
      const user = await JSON.parse(
        localStorage.getItem(process.env.USER)
      );
      const { data } = await axios.post(`${setAvatarRoute}/${user._id}`, {
        image: avatars[selectedAvatar],
      });

      // if avatar image is set
      if (data.isSet) {
        user.isAvatarImageSet = true;
        user.avatarImage = data.image;
        localStorage.setItem(
          process.env.USER,
          JSON.stringify(user)
        );
        navigate("/");
      } else {
        console.log(data);
        showToast("Error setting avatar. Please try again");
      }
    }
  };

  // Fetch Avatars
  const fetchAvatars = async () => {
    setIsLoading(true);
    const data = [];
    // foreach doesn't work with APIs
    for (let i = 0; i < 4; i++) {
      const image = await axios.get(
        `${API_URI}/${Math.round(Math.random() * 1000)}?apikey=${
          process.env.REACT_APP_MULTIAVATAR_API_KEY
        }`
      );
      // change image to base64
      const buffer = new Buffer(image.data);
      data.push(buffer.toString("base64"));
    }

    setAvatars(data);
    setIsLoading(false);
    setSelectedAvatar(undefined);
  };

  // fetch avatars when page is first loaded
  useEffect(() => {
    fetchAvatars();
  }, []);

  return (
    <>
      {isLoading ? (
        // Loader
        <Container>
          <img src={loader} alt="Loading..." className="loader" />
        </Container>
      ) : (
        <Container>
          <div className="title-container">
            <h1>Pick an avatar as your profile picture</h1>
          </div>

          {/* Avatars */}
          <div className="avatars">
            {avatars.map((avatar, i) => {
              return (
                <div
                  key={i}
                  className={`avatar ${selectedAvatar === i ? "selected" : ""}`}
                >
                  <img
                    src={`data:image/svg+xml;base64,${avatar}`}
                    alt={`Avatar ${i + 1}`}
                    onClick={() => setSelectedAvatar(i)}
                  />
                </div>
              );
            })}
          </div>
          <div className="btn-container">
            {/* set profile picture */}
            <button className="submit-btn" onClick={setProfilePicture}>
              Set as Profile Picture
            </button>

            {/* Fetch new avatars */}
            <button
              className="reload-btn"
              onClick={fetchAvatars}
              title="Load New Avatars"
            >
              <svg
                xmlns="http://www.w3.org/2000/svg"
                width="32"
                height="32"
                fill="currentColor"
                viewBox="0 0 16 16"
              >
                <path
                  fillRule="evenodd"
                  d="M8 3a5 5 0 1 0 4.546 2.914.5.5 0 0 1 .908-.417A6 6 0 1 1 8 2v1z"
                />
                <path d="M8 4.466V.534a.25.25 0 0 1 .41-.192l2.36 1.966c.12.1.12.284 0 .384L8.41 4.658A.25.25 0 0 1 8 4.466z" />
              </svg>
            </button>
          </div>
        </Container>
      )}

      {/* Toast container */}
      <ToastContainer />
    </>
  );
};

// Styled Components
const Container = styled.div`
@keyframes color {
  0%   { background: #122D42; }
  20%  { background: #525D68; }
  40%  { background: #516587; }
  60%  { background: #64B2AF; }
  80%  { background: #84A0D1; }
  100% { background: #122D42; }
}
  background: #122D42; /* Fallback */
  animation: color 9s infinite linear;
  text-align: center;
  padding: 2em;

  display: flex;
  justify-content: center;
  align-items: center;
  flex-direction: column;
  gap: 3rem;
  background-color: #131324;
  height: 100vh;
  width: 100vw;
  .loader {
    max-inline-size: 100%;
  }

  .title-container {
    h1 {
      color: #fff;
    }
  }

  .avatars {
    display: flex;
    gap: 2rem;

    .avatar {
      border: 0.4rem solid transparent;
      padding: 0.4rem;
      border-radius: 5rem;
      display: flex;
      justify-content: center;
      align-items: center;
      transition: 0.5s ease-in-out;

      img {
        height: 6rem;
      }
    }

    .selected {
      border: 0.4rem solid #4e0eff;
    }
  }

  .btn-container {
    display: flex;
    justify-content: center;
    align-items: center;
    gap: 0.5rem;

    .submit-btn {
      background-color: #fff;
      color: darkgray;
      padding: 1rem 2rem;
      border: none;
      font-weight: bold;
      cursor: pointer;
      border-radius: 0.4rem;
      font-size: 1rem;
      text-transform: uppercase;
      transition: 0.2s ease-in-out;

      &:hover {
        background-color: #997af0;
      }
    }

    .reload-btn {
      display: grid;
      place-items: center;
      background-color: #4e0eff;
      color: #fff;
      height: 100%;
      padding: 0 1rem;
      border: none;
      font-weight: bold;
      cursor: pointer;
      border-radius: 0.4rem;
      font-size: 1rem;
      text-transform: uppercase;
      transition: 0.2s ease-in-out;

      &:hover {
        background-color: #997af0;
      }
    }
  }
`;

export default SetAvatar;
